mapper.update:
update nursing_level
name=#{name}, lplan_id=#{planId}, fee=#{fee}, status=#{status}, description=#{description}, update_by = #{updateBy}, remark = #{remark}, update_time = #{updateTime},
where id =#{id}

mapper.select:
select nl.*,su.real_name creator,np.plan_name planName from nursing_level nl left join sys_user su on nl.create_by=su.id left join nursing_plan np on np.id = nl.lplan_id
name like concat('%',#{name},'%') and status=#{status}
order by create_time desc
