mapper.update:
update nursing_project
name = #{name}, order_no = #{orderNo}, unit = #{unit}, price = #{price}, image = #{image}, nursing_requirement = #{nursingRequirement}, status = #{status}, update_by = #{updateBy}, remark = #{remark}, create_time = #{createTime}, update_time = #{updateTime},
where id = #{id}

mapper.select:
select np.*,su.real_name creator from nursing_project np left join sys_user su on su.id=np.create_by
name like concat('%',#{name},'%') and status=#{status}
order by order_no ,create_time desc
